import React, { useState } from 'react';
import { Dialog, DialogTitle, DialogContent, DialogActions, Button, TextField, Alert, CircularProgress, Typography, Box, InputAdornment } from '@mui/material';
import { useAuth } from '../contexts/AuthContext';
import MailOutlineIcon from '@mui/icons-material/MailOutline';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';

interface Props { open: boolean; onClose: () => void; onSuccess?: () => void; onOpenRegister?: () => void }

const LoginModal: React.FC<Props> = ({ open, onClose, onSuccess, onOpenRegister }) => {
  const { login } = useAuth();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleLogin = async () => {
    setError('');
    if (!email || !password) { setError('Email and password are required'); return; }
    setLoading(true);
    try {
      await login(email, password);
      onSuccess?.();
      onClose();
    } catch (e: any) {
      setError(e.response?.data?.message || 'Login failed');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Dialog open={open} onClose={onClose} fullWidth maxWidth="xs">
      <DialogTitle disableTypography sx={{ pb: 0 }}>
        <Typography component="div" variant="h5" fontWeight={700} color="primary">Sign in</Typography>
        <Typography component="div" variant="body2" color="text.secondary">Welcome back! Please sign in to continue</Typography>
      </DialogTitle>
      <DialogContent sx={{ pt: 2 }}>
        {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}
        <Box sx={{ mt: 1 }}>
          <TextField
            fullWidth
            label="Email"
            type="email"
            margin="normal"
            value={email}
            onChange={e => setEmail(e.target.value)}
            InputProps={{ startAdornment: (<InputAdornment position="start"><MailOutlineIcon fontSize="small" /></InputAdornment>) }}
          />
          <TextField
            fullWidth
            label="Password"
            type="password"
            margin="normal"
            value={password}
            onChange={e => setPassword(e.target.value)}
            InputProps={{ startAdornment: (<InputAdornment position="start"><LockOutlinedIcon fontSize="small" /></InputAdornment>) }}
          />
        </Box>
      </DialogContent>
      <DialogActions sx={{ justifyContent: 'space-between', px: 3, pb: 2 }}>
        <Button onClick={onOpenRegister} color="secondary">Create New Account</Button>
        <Box>
          <Button onClick={onClose} sx={{ mr: 1 }}>Cancel</Button>
          <Button variant="contained" onClick={handleLogin} disabled={loading} startIcon={loading ? <CircularProgress size={20} /> : undefined}>
            {loading ? 'Signing In...' : 'Login'}
          </Button>
        </Box>
      </DialogActions>
    </Dialog>
  );
};

export default LoginModal;
